INSERT INTO translations (id, key, language, value, namespace, "updatedAt") VALUES ('bd6345b4-2a1f-4355-b5dc-34fcf45bf0b0', 'privacy.data_usage_desc', 'en', 'Your information is used to: (1) Connect you with veterinary clinics during emergencies; (2) Send emergency alerts via WhatsApp and email; (3) Improve service quality; (4) Comply with legal obligations. We do not sell your data.', 'common', NOW()) ON CONFLICT (key, language) DO UPDATE SET value = EXCLUDED.value;
INSERT INTO translations (id, key, language, value, namespace, "updatedAt") VALUES ('d0bbcf84-0eeb-4c20-99ed-0915e908b765', 'privacy.data_usage_desc', 'zh-HK', '您的資料用於：(1) 在緊急情況下為您聯絡獸醫診所；(2) 透過WhatsApp及電郵發送緊急警報；(3) 改善服務質素；(4) 履行法律責任。我們不會出售您的資料。', 'common', NOW()) ON CONFLICT (key, language) DO UPDATE SET value = EXCLUDED.value;
INSERT INTO translations (id, key, language, value, namespace, "updatedAt") VALUES ('b1f6bdd7-64a0-4cf6-b360-f2421588ab41', 'privacy.data_retention_title', 'en', 'Data Retention', 'common', NOW()) ON CONFLICT (key, language) DO UPDATE SET value = EXCLUDED.value;
