INSERT INTO translations (id, key, language, value, namespace, "updatedAt") VALUES ('4c417067-74f6-4e9c-afec-66fbf20ccd95', 'privacy.data_retention_desc', 'en', 'We retain: (1) Emergency requests for 12 months; (2) Pet profiles until deletion requested; (3) Audit logs for 7 years (legal compliance); (4) User accounts until deletion requested. You may request data deletion at any time.', 'common', NOW()) ON CONFLICT (key, language) DO UPDATE SET value = EXCLUDED.value;
