INSERT INTO translations (id, key, language, value, namespace, "updatedAt") VALUES ('5ab14a27-cdf1-4e05-9b2e-078cbb236b0c', 'privacy.complaints_desc', 'en', 'To lodge a complaint: (1) Contact us at privacy@petsos.hk; (2) Contact Hong Kong Privacy Commissioner: enquiry@pcpd.org.hk; (3) For EU residents: Contact your local supervisory authority.', 'common', NOW()) ON CONFLICT (key, language) DO UPDATE SET value = EXCLUDED.value;
